#include <bits/stdc++.h>using namespace std;int main(){//    freopen("input.txt", "r", stdin);    ios::sync_with_stdio(0);    cin.tie(0);    cout.tie(0);    int t;    int n, m, m1;    string s;    int sLen;    int cnt;    cin >> t;    for(int t1 = 0 ; t1 < t ; t1++)    {        cin >> n >> m;        cin >> s;        sLen = s.length();        if(sLen > m)        {            cout << "0\n";            for(cnt = 1 ; cnt < n ; cnt++){                cin >> s;            }            continue;        }        m1 = m;        m1 -= sLen;        for(cnt = 1 ; cnt < n ; cnt++)        {            cin >> s;//            cout << " n " << n << " m " << m << " s.length() " << s.length() << "\n";            m1 -= s.length();            if(m1 < 0)            {                break;            }        }        cout << cnt << "\n";        for(cnt++ ; cnt < n ; cnt++){            cin >> s;        }    }    return 0;}